    public static void main(String[] args) {
        Character hatgirl = new Character("Красная шапочка", 100, 100, 25);
        Situation start = windowsStory();
        Story story = new Story(start, hatgirl);
        Scanner scanner = new Scanner(System.in);
        while (!story.isEnd()) {
            System.out.println(story.getCurrent().getName());
            int a = scanner.nextInt();
            story.go(a);
        }
        System.out.println(story.getCurrent().getName());
    }
